    @org.junit.jupiter.api.Test
    void TestComplexSearch() throws JsonProcessingException {
        API api = new API();
        Ingredient ingredient1 = new Ingredient("2023-01-01", "Tomato", false);
        Ingredient ingredient2 = new Ingredient("2023-02-15", "Milk", true);
        List<Ingredient> l_ingredients = new ArrayList<Ingredient>();
        l_ingredients.add(ingredient1);
        l_ingredients.add(ingredient2);
        Fridge fridge = new Fridge(l_ingredients);
        User user = new User("test",null,null);
        user.setFridge(fridge);
        List<Ingredient> l_testIngredients = new ArrayList<Ingredient>();
        Ingredient testIngredient1 = new Ingredient("null", "tomatoes", false);
        Ingredient testIngredient2 = new Ingredient("null", "unripened goat's milk cheese", false);
        Ingredient testIngredient3 = new Ingredient("null", "parmesan", false);
        Ingredient testIngredient4 = new Ingredient("null", "basil", false);
        Ingredient testIngredient5 = new Ingredient("null", "basil", false);
        Ingredient testIngredient6 = new Ingredient("null", "salt & pepper", false);
        Ingredient testIngredient7 = new Ingredient("null", "olive oil", false);
        l_testIngredients.add(testIngredient1);
        l_testIngredients.add(testIngredient2);
        l_testIngredients.add(testIngredient3);
        l_testIngredients.add(testIngredient4);
        l_testIngredients.add(testIngredient5);
        l_testIngredients.add(testIngredient6);
        l_testIngredients.add(testIngredient7);
        List<Double> l_testQuantityIngredients = new ArrayList<Double>();
        l_testQuantityIngredients.add(2.0);
        l_testQuantityIngredients.add(0.25);
        l_testQuantityIngredients.add(2.0);
        l_testQuantityIngredients.add(1.0);
        l_testQuantityIngredients.add(1.0);
        l_testQuantityIngredients.add(1.0);
        l_testQuantityIngredients.add(4.0);
        List<Recipe> testRecipies = new ArrayList<>();
        testRecipies.add(new Recipe("https://spoonacular.com/recipeImages/633852-312x231.jpg","baked tomatoes", l_testIngredients,l_testQuantityIngredients,45));
        try {
            List<Recipe> response = api.ComplexSearch(user,1,"max-used-ingredients",true,true,false);
            System.out.println(response);
            assertThat(response).isNotNull();
            for(int i=0;i<response.size();i++){
                assertThat(response.get(i).getTitle()).isEqualTo(testRecipies.get(i).getTitle());
                assertThat(response.get(i).getLinkToImage()).isEqualTo(testRecipies.get(i).getLinkToImage());
                assertThat(response.get(i).getCookingTime()).isEqualTo(testRecipies.get(i).getCookingTime());
                for(int j=0;j<response.get(i).getL_ingredients().size();j++){
                    assertThat(response.get(i).getL_ingredients().get(j).getName()).isEqualTo(testRecipies.get(i).getL_ingredients().get(j).getName());
                    assertThat(response.get(i).getL_quantityIngredients().get(j)).isEqualTo(testRecipies.get(i).getL_quantityIngredients().get(j));
                }

            }
        } catch (Exception e) {
            e.printStackTrace();
        }

    }

